// --------------------
// Normalize input events
// --------------------
const rawEvents = $input.all();

const events = rawEvents
  .map(e => e.json)
  .filter(e => e?.start?.dateTime && e?.end?.dateTime);

// --------------------
// Configuration
// --------------------
const WORK_START_HOUR = 11; // 11 AM
const WORK_END_HOUR = 19;   // 7 PM

// --------------------
// Helpers
// --------------------
function formatTime(hour, minute = 0) {
  const period = hour >= 12 ? "PM" : "AM";
  const displayHour = hour > 12 ? hour - 12 : hour === 0 ? 12 : hour;
  return `${displayHour}:${minute.toString().padStart(2, "0")} ${period}`;
}

function isSunday(date) {
  return date.getDay() === 0;
}

// Force IST (Asia/Kolkata)
function getISTDate() {
  const ist = new Date(
    new Date().toLocaleString("en-US", { timeZone: "Asia/Kolkata" })
  );
  ist.setHours(0, 0, 0, 0);
  return ist;
}

// Slot availability
function isSlotAvailable(slotStart, slotEnd) {
  if (events.length === 0) return true;

  for (const event of events) {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);

    if (slotStart < eventEnd && slotEnd > eventStart) {
      return false;
    }
  }
  return true;
}

// --------------------
// Main Logic
// --------------------
const today = getISTDate();
const result = [];

let daysProcessed = 0;
let offset = 0;

while (daysProcessed < 2) {
  const date = new Date(today);
  date.setDate(today.getDate() + offset);
  offset++;

  if (isSunday(date)) continue;

  const slots = {};

  for (let hour = WORK_START_HOUR; hour < WORK_END_HOUR; hour++) {
    const start = new Date(date);
    start.setHours(hour, 0, 0, 0);

    const end = new Date(date);
    end.setHours(hour + 1, 0, 0, 0);

    const label = `${formatTime(hour)} - ${formatTime(hour + 1)}`;
    slots[label] = isSlotAvailable(start, end)
      ? "Available"
      : "Not Available";
  }

  // IMPORTANT: format date in IST (not ISO)
  const formattedDate = date.toLocaleDateString("en-CA", {
    timeZone: "Asia/Kolkata",
  });

  result.push({
    date: formattedDate,
    slots,
  });

  daysProcessed++;
}

return {
  data: result
};

